import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent {

}
<div class="container">
  <nav>
    <ul>
      <li><a routerLink="/home" routerLinkActive="active" class="left" ><img id="logo" src="../assets/ss-flights.png"></a></li>
      <li><a routerLink="/help" routerLinkActive="active" class="right" id="help">❔  Help</a></li>
      <li><a routerLink="/my-trips" routerLinkActive="active" class="right">✈︎  My Trips</a></li>
      <li><a routerLink="/contact" routerLinkActive="active" class="right">☎  Contact</a></li>
      <li><a routerLink="/log-in" routerLinkActive="active" class="right" id="log-in">Log In</a></li>
      <li><a routerLink="/sign-up" routerLinkActive="active" class="right">Sign Up</a></li>
    </ul>
  </nav>
</div>
