import { Component } from '@angular/core';

import { Search } from '../search-class';

@Component({
  selector: 'app-search-form',
  templateUrl: './search-form.component.html',
  styleUrls: ['./search-form.component.css'],
})
export class SearchFormComponent {
  departureDate: string;
  returnDate: string;
  model = new Search('Skopje', 'Barcelona', '03 March', '05 March');
  submitted = false;

  constructor() {
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(today.getDate() + 1);

    this.departureDate = today.toISOString().split('T')[0];
    this.returnDate = tomorrow.toISOString().split('T')[0];
  }

  onSubmit() {
    this.submitted = true;
  }
}
<div class="form-group">
  <div class="from-container containers">
    <label for="fromLocation containers">From</label>
    <input type="text" placeholder="Origin" />
  </div>
  <div class="to-container containers">
    <label for="toLocation containers">To</label>
    <input type="text" placeholder="Destination" />
  </div>
  <div class="departure-container containers">
    <label for="departure">Departure</label>
    <input type="date" [(ngModel)]="departureDate" />
  </div>
  <div class="return-container containers">
    <label for="return">Return</label>
    <input type="date" [(ngModel)]="returnDate" />
  </div>
  <a routerLink="/search" routerLinkActive="active" class="button">✈︎</a>
</div>
