@Component({
  selector: 'app-flight-details',
  templateUrl: './flight-details.component.html',
  styleUrls: ['./flight-details.component.css']
})
export class FlightDetailsComponent {

}
<div class="container">
  <div id="departure-container">
    <h2>10:45</h2>
    <h4>London</h4>
