import { Component } from '@angular/core';
import { Destination } from '../destination';
import { DestinationList } from '../destinations-list';

@Component({
  selector: 'app-popular-destinations',
  templateUrl: './popular-destinations.component.html',
  styleUrls: ['./popular-destinations.component.css']
})
export class PopularDestinationsComponent {

  destinations: Destination[] = DestinationList

}
<div class="container">
  <h1>Popular Destinations</h1>
  <div class="destination-list">
    <div class="destination" *ngFor="let destination of destinations">
      <img id="photo" [src] = "destination.photo">
      <div class="destination-title">{{destination.city}}</div>
    </div>
  </div>
</div>
