@Component({
  selector: 'app-help-page',
  templateUrl: './help-page.component.html',
  styleUrls: ['./help-page.component.css'],
})
export class HelpPageComponent {
  showAnswer1: boolean = false;
  showAnswer2: boolean = false;
  showAnswer3: boolean = false;
}
