import { Component, HostListener } from '@angular/core';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css']
})
export class HomePageComponent {
  showScrollToTop = false;

  // Scroll the page to the top when the user clicks the scroll-to-top button
  scrollToTop() {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }

  // Listen for the window's scroll event to show or hide the scroll-to-top button
  @HostListener('window:scroll', [])
  onWindowScroll() {
    // If the user has scrolled down 100 pixels or more from the top of the page, show the button
    if (window.pageYOffset > 100) {
      this.showScrollToTop = true;
    } else {
      this.showScrollToTop = false;
    }

    // Hide the button when the user is at the top of the page
    if (window.pageYOffset === 0) {
      this.showScrollToTop = false;
    }
  }

}
<div class="container">
    <img id="main-photo" src="../assets/main-photo.jpg">
    <h1 id="main-slogan">We Make your dreams come true</h1>
  </div>

  <div class="scroll-to-top" id="scroll-to-top" (click)="scrollToTop()">
    <a href="#top">⇑</a>
  </div>

  <div class="search-form-container">
    <app-search-form></app-search-form>
  </div>
  <app-popular-destinations></app-popular-destinations>
  <app-homepage-extra-info></app-homepage-extra-info>
<app-footer></app-footer>
